//7) using nested for loop display following :
//
//A  a
//A  b
//A  c
//
//B  a
//B  b
//B  c
//
//C  a
//C  b
//C  c

public class Pattern_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(char i=65;i<68;i++) {
			for(char j=97;j<100;j++) {
				System.out.println(i+" "+j);
			}
		}

	}

}
